        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          prompt,
          model,
          max_tokens: 1000
        })
      });
      const data = await response.json();
      console.log(data, 'bark')

      return data.choices[0].text
    } catch (error) {
      console.log('Error', error);
    }
    return null
  }

