import React, {useState} from 'react'
import logo from './logo.svg';
import './App.css';

function App() {
  const [inputText, setInputText] = useState('')
  const [outputText, setOutputText] = useState('')
  const [questionAnswerText, setQuestionAnswerText] = useState('')
  const [cardsList, setCardsList] = useState(null)
  const [cards, setCards] = useState(cardInfo)

  const apiKey = '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'
  const model = 'text-davinci-003'

  const Flashcard = ({flashcard}) => {
    //cha cha real smooth asjkcnkmmjhghhbjnkjhhvjbknnjbhhjbnhbjnhvghbjhvghbjhvhbjhbjnj
    const [flip, setFlip] = useState(false)

    return (
      <div className={`card ${flip ? 'flip' : ""}`} onClick = {() => setFlip(!flip)}>
        <div className = "front">
          {flashcard.question}
          <div className = "flashcard-options">
            {flashcard.option.map(option => {
              return <div classname = "flashcard-option">{option}</div>
            })}
          </div>
        {flip? card.answer: flashcard.question} 
        </div>
        <div className = "back">{flashcard.answer}</div>
      </div>
    )
  }
// dxfghujighfdcfvghjkkhgfdxvcbvnbhjytrdxcbvnbhygtfg
  const FlashcardList = ({flashcards}) => {
    return (
      <div className="card-grid">
        {flashcards.map(flashcard => {
          return <Flash flashcard={flashcard} key={card.id}/>
        })}
      </div>
    )
  }

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const askChatGpt = async (prompt) => {
    console.log(prompt)

    try {
      const response = await fetch ('https://api.openai.com/v1/completions',{
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          prompt,
          model,
          max_tokens: 1000
        })
      });
      const data = await response.json();
      console.log(data, 'bark')

      return data.choices[0].text
    } catch (error) {
      console.log('Error', error);
    }
    return null
  }

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    let newinput = "'" + inputText + "' break this down into question and answers"

    let questionAnswerOutput = await askChatGpt(newinput)
    console.log(questionAnswerOutput)

    setQuestionAnswerText(questionAnswerOutput)
    let text = questionAnswerOutput
    let cardsText = text.split('Q: ')

    let cardsInfo = []
    let counter = 0
    cardsText.forEach(cardText => {
      let cardInfo = {
        id: counter,
        question: cardText.split('A: ')[0],
        answer: cardText.split('A: ')[1]
      }
      counter += 1
      cardsInfo.push(cardInfo)
    })

    setCardsList(cardsInfo)

  };


  return (
    <div className="App">
      <form onSubmit={handleFormSubmit}>
        <input type="text" value={inputText} onChange={handleInputChange} />
        <button type="submit">Send</button>
      </form>
      <p>{questionAnswerText}</p>
      <br/>
      {cardsList && <div>
        <ul>
          {cardsList.map((card) => [
            <div key={card.id}>
              <p>{card.question}</p>
              <p> answe {card.answer}</p>
            </div>
          ])}
        </ul>
        </div>}
    </div>
  );
}

export default App;
